mapper.select:
select

from refund


    AND  oid = #{oid,jdbcType=INTEGER}


    AND id = #{id,jdbcType=INTEGER}


    AND userid = #{userid,jdbcType=VARCHAR}


    AND no = #{no,jdbcType=VARCHAR}

mapper.delete:
delete from refund


    AND id = #{id,jdbcType=INTEGER}


    AND  no = #{no,jdbcType=VARCHAR}

mapper.insert:
insert into refund (id, oid, userid,no,
  price, createdAt, updatedAt
  )
values (#{id,jdbcType=INTEGER}, #{oid,jdbcType=INTEGER},#{userid,jdbcType=VARCHAR}, #{no,jdbcType=VARCHAR},
  #{price,jdbcType=DECIMAL}, #{createdat,jdbcType=INTEGER}, #{updatedat,jdbcType=INTEGER}
  )
